import React,{useState} from 'react'

export default function TextForm(props) {
  const [text , setText] = useState("Enter text here");

  const upClick = ()=>{
    console.log("button clicked");
    setText(text.toUpperCase());
  };
  const lowClick = ()=>{
    console.log("button clicked");
    setText(text.toLowerCase());
  };

  const senClick = ()=>{
    console.log("button clicked");
    const sentences = text.split('. ').map(sentence => {
    return sentence.charAt(0).toUpperCase() + sentence.slice(1).toLowerCase();
    });
  setText(sentences.join('. '));
  };

  const capitalClick = ()=>{
    console.log("button clicked");
    const words = text.split(" ").map(word => {
      return word.charAt(0).toUpperCase() + word.slice(1).toLowerCase();
    })
  };

  const clearClick = ()=>{
    console.log("button clicked");
    setText("");
  };
  const OnChange = (event)=>{
    console.log("Text area changed");
    setText(event.target.value)
  };
  // console.log(text[0])
  return (
    <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
        <textarea 
        className="form-control"
        onChange={OnChange}
        value={text}
        id="myBox" 
        rows="8"></textarea>
        <button className="btn btn-primary mx-2" onClick = {upClick}>Convert to uppercase</button>
        <button className="btn btn-primary mx-2" onClick = {lowClick}>Convert to Lowercase</button>
        <button className="btn btn-primary mx-2" onClick = {senClick}>Convert to sentence</button>
        <button className="btn btn-primary mx-2" onClick = {senClick}>Convert to sentence</button>
        <button className="btn btn-primary mx-2" onClick = {capitalClick}>convert to Capital</button>
        </div>
        <div className="container my-3">
          <h2>Your text summary</h2>
          <p>{text.split(" ").length} words and {text.length} letters</p>
          <p>You will probably need {0.33 * text.split(" ").length} seconds to read</p>
          <h3>Preview</h3>
          <p>{text}</p>
        </div>
    </div>
  )
}
