import './App.css';
import Navbar from './Components/Navbar.js';
import TextForm from './Components/TextForm.js';
import About from './Components/About.js';
import Alert from './Components/Alert.js'
import { useState } from 'react';

function App() {
  const[mode , setMode] = useState('light')
  const[alert , setAlert] = useState(null)
  // const [btnStyle , setBtnStyle] = useState({backgroundColor : ''})

  const showAlert = (messege, type)=>{
    setAlert({
      msg:messege,
      type:type
    })
    setTimeout(() => {
      setAlert (null);
    }, 1500);
  }
  const toDarkMode = ()=>{
    if(mode === 'light' || 'red'){
      setMode('dark')
      document.body.style.backgroundColor='#042743'
      showAlert('Dark mode has been enabled','success')
    }else{
      setMode('light')
      document.body.style.backgroundColor='white'
      showAlert('Light mode has been enabled','success')
    }
  }
  const toRedMode = ()=>{
    if(mode === 'light' || 'Dark'){
      setMode('red')
      document.body.style.backgroundColor='red'
      showAlert('Red mode has been enabled','success')
    }else{
      setMode('light')
      document.body.style.backgroundColor='white'
      showAlert('White mode has been enabled','success')
    }
}
  return (
    <>
      <Navbar title="MYNAV" home="Home" mode={mode} toRedMode={toRedMode} toDarkMode={toDarkMode}/>
      {/* <Alert/> */}
      <Alert alert={alert}/>
      <div className='container my-3'>
        <TextForm heading="Enter your text" mode={mode} toRedMode={toRedMode} toDarkMode={toDarkMode} showAlert={showAlert} />
      </div>
      <About mode={mode} toRedMode={toRedMode} toDarkMode={toDarkMode}></About>
    </>
  );
}
export default App;
