import './App.css';
import Navbar from './Components/Navbar.js';
import TextForm from './Components/TextForm.js';
import About from './Components/About.js';
import { useState } from 'react';

function App() {
  const[mode , setMode] = useState('light')
  const[Alert , setAlert] = useState('This is a alert!')

  const showAlert = (Alert , type) =>{
    <div class="alert alert-primary" role="alert">
      A simple primary alertâ€”check it out!
    </div>

  }
  const toggleMode = ()=>{
    if(mode === 'light'){
      setMode('dark')
      document.body.style.backgroundColor='#042743'
    }else{
      setMode('light')
      document.body.style.backgroundColor='white'
    }
  }
  return (
    <>
      <Navbar title="MYNAV" home="Home" mode={mode} toggleMode={toggleMode}showAlert={showAlert}/>
      <div className='container my-3'>
        <TextForm heading="Enter your text" mode={mode} toggleMode={toggleMode} showAlert={showAlert}/>
      </div>
      <About mode={mode} toggleMode={toggleMode}></About>
    </>
  );
}

export default App;
