import './App.css';
import Navbar from './Components/Navbar.js';
import TextForm from './Components/TextForm.js';
import About from './Components/About.js';
import Alert from './Components/Alert.js'
import { useState } from 'react';

function App() {
  const[mode , setMode] = useState('light')
  const[alert , setAlert] = useState(null)
  const [btnStyle , setBtnStyle] = useState({backgroundColor : ''})

  const showAlert = (messege, type)=>{
    setAlert({
      msg:messege,
      type:type
    })
    setTimeout(() => {
      setAlert (null);
    }, 1500);
  }
  const toggleMode = ()=>{
    if(mode === 'light'){
      setMode('dark')
      document.body.style.backgroundColor='#042743'
      showAlert('Dark mode has been enabled','success')
      // setBtnStyle({backgroundColor:'red'})
    }else{
      setMode('light')
      document.body.style.backgroundColor='white'
      showAlert('Light mode has been enabled','success')
      // setBtnStyle({backgroundColor:'#0d6efd'})
    }
  const toRedMode = ()=>{
    if(mode === 'red'){
      setMode('light')
      document.body.style.backgroundColor='white'
      showAlert('Light mode has been enabled','success')
      // setBtnStyle({backgroundColor:'red'})
    }else{
      setMode('red')
      document.body.style.backgroundColor='red'
      showAlert('red mode has been enabled','success')
      // setBtnStyle({backgroundColor:'#0d6efd'})
    }
  }
  return (
    <>
      <Navbar title="MYNAV" home="Home" mode={mode} toggleMode={toggleMode}/>
      {/* <Alert/> */}
      <Alert alert={alert}/>
      <div className='container my-3'>
        <TextForm heading="Enter your text"btnStyle={btnStyle} mode={mode} toggleMode={toggleMode} showAlert={showAlert} />
      </div>
      <About mode={mode} toggleMode={toggleMode}></About>
    </>
  );
}
}
export default App;
